mapper.select:
SELECT 

FROM copyuser a

WHERE a.id = #{id}

SELECT 

FROM copyuser a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_name = #{userName}


		AND a.sex = #{sex}


		AND a.req_base = #{reqBase}


		AND a.user_number = #{userNumber}


		AND a.grade = #{grade}


		AND a.student_class = #{studentClass}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM copyuser a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE copyuser SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
